use itertools::Itertools;

fn main() {
    println!("1. {}", solve(1));
    println!("2. {}", solve(2));
    println!("3. {}", solve(3));
}

fn solve(part: usize) -> usize {
    get_input(part - 1)
        .as_bytes()
        .into_iter()
        .chunks(part)
        .into_iter()
        .map(|baddies| {
            let baddies: Vec<u8> = baddies.cloned().collect();
            let number_of_baddies = baddies
                .iter()
                .filter(|&&it| it != b'x')
                .count();
            // println!("baddies: {:?}, #: {number_of_baddies}", baddies);
            let potions = |baddie: &u8| -> usize {
                match &baddie {
                    b'A' => 0,
                    b'B' => 1,
                    b'C' => 3,
                    b'D' => 5,
                    _ => 0,
                }
            };
            let bonus_potions = number_of_baddies * 
                (number_of_baddies.checked_sub(1).unwrap_or_default());
            bonus_potions + (baddies.iter().map(|baddie| potions(baddie)).sum::<usize>())
        }).sum()
}

fn get_input(part: usize) -> &'static str {
    let inputs = [
        "",
        "",
        "",
    ];
    inputs[part]
}

#[test]
fn default() {
    assert_eq!(1354, solve(1));
    assert_eq!(5639, solve(2));
    assert_eq!(28180, solve(3));
}